n    line-height: 1.7;\n    color: #292929;\n    margin-bottom: 1.2rem;\n}\n\n.contenedor-tarjetas-columnas {\n    display: grid;\n    justify-content: center;\n    grid-template-columns: repeat(4, 300px);\n    gap: 75px;\n    margin: 50px 0;\n    padding-top: 50px;\n    width: 100%;\n}\n\n.tarjeta-estilo {\n    background: var(--color-primario);\n    border-radius: 30px;\n    overflow: hidden;\n    box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n    transition: transform 0.3s ease, box-shadow 0.3s ease;\n    display: flex;\n    flex-direction: column;\n    height: 100%;\n}\n\n.tarjeta-estilo:hover {\n    transform: translateY(-10px);\n    box-shadow: 0 15px 30px rgba(0,0,0,0.15);\n}\n\n.tarjeta-imagen {\n    height: 250px;\n    overflow: hidden;\n}\n\n.tarjeta-imagen img {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n    transition: transform 0.5s ease;\n}\n\n.tarjeta-estilo:hover .tarjeta-imagen img {\n    transform: scale(1.05);\n}\n\n  .tarjeta-contenido {\n    padding: 25px;\n    flex-grow: 1;\n    display: flex;\n    flex-direction: column;\n}\n\n.tarjeta-contenido h3 {\n    font-family: \'Exo 2\';\n    font-size: 1.8rem;\n    letter-spacing: 0.5px;\n    margin-bottom: 10px;\n    color: var(--color-texto);\n}\n\n.tarjeta-contenido p {\n    font-family: \'Exo 2\';\n    color: var(--color-texto);\n    margin-bottom: 20px;\n    line-height: 1.6;\n    flex-grow: 1;\n}\n\n.tarjeta-boton {\n    display: inline-block;\n    padding: 12px 25px;\n    background: var(--color-terciario);\n    color: var(--color-primario);\n    text-decoration: none;\n    border-radius: 30px;\n    font-family: \'Bebas Neue\', sans-serif;\n    font-size: 1.2rem;\n    font-weight: 400;\n    transition: all 0.3s ease;\n    text-align: center;\n    margin-top: auto;\n}\n\n.tarjeta-boton:hover {\n    transform: translateY(10px);\n}\n\n.pie-de-pagina {\n    display: flex;\n    flex-direction: column;\n    align-items: center;\n    justify-content: center;\n    padding: 20px 0px;\n    margin-top: 250px;\n    margin-bottom: 0;\n    background: linear-gradient(0deg, var(--color-secundario)25%, #303030, var(--color-secundario)75%);\n    height: 300px;\n    min-height: 100%;\n    width: 100%;\n    max-width: 100vw;\n    border-radius:0px;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.container-redes-sociales {\n    display: flex;\n    justify-content: center;\n    gap: 20px;\n    width: 80%;\n}\n\n.redes-sociales {\n    padding: 12px 24px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 30px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    background: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n    margin: 0;\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n}\n\n.redes-sociales:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    transform: translateY(-3px);\n}\n\n.texto-pie {\n    padding-top: 200px;\n    font-family: \'Exo 2\';\n    font-size: 12px;\n    color: var(--color-texto);\n}\n</style>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n<!-- /wp:html -->', '', '', 'inherit', 'closed', 'closed', '', '23-revision-v1', '', '', '2025-04-01 17:44:39', '2025-04-01 15:44:39', '', 23, 'http://localhost/wordpress/?p=24', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(25, 2, '2025-04-01 17:45:14', '2025-04-01 15:45:14', '<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Fórmula 1</title>\n    <link rel=\"stylesheet\" href=\"HeaderConBotonesCSS.css\">\n    <link href=\"https://fonts.googleapis.com/css2?family=Exo+2:ital,wght@0,100..900;1,100..900&family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n</head>\n<body>\n    <div class=\"barra-superior\">\n        <div class=\"encabezado-superior\">\n            <div class=\"botones-izquierda\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">PILOTOS</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Verstappen</a>\n                        <a href=\"#\">Tsunoda</a>\n                        <a href=\"#\">Norris</a>\n                        <a href=\"#\">Piastri</a>\n                        <a href=\"#\">Leclerc</a>\n                        <a href=\"#\">Hamilton</a>\n                        <a href=\"#\">Russell</a>\n                        <a href=\"#\">Antonneli</a>\n                        <a href=\"#\">Alonso</a>\n                        <a href=\"#\">Stroll</a>\n                        <a href=\"#\">Gasly</a>\n                        <a href=\"#\">Doohan</a>\n                        <a href=\"#\">Ocon</a>\n                        <a href=\"#\">Bearman</a>\n                        <a href=\"#\">Lawson</a>\n                        <a href=\"#\">Hadjar</a>\n                        <a href=\"#\">Albon</a>\n                        <a href=\"#\">Sainz</a>\n                        <a href=\"#\">Hülkenberg</a>\n                        <a href=\"#\">Bortoleto</a>\n                    </div>\n                </div>\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CONSTRUCTORES</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">McLaren</a>\n                        <a href=\"#\">Ferrari</a>\n                        <a href=\"#\">Red Bull</a>\n                        <a href=\"#\">Mercedes</a>\n                        <a href=\"#\">Aston Martin</a>\n                        <a href=\"#\">Alpine</a>\n                        <a href=\"#\">Haas</a>\n                        <a href=\"#\">RB</a>\n                        <a href=\"#\">Williams</a>\n                        <a href=\"#\">Kick Sauber</a>\n                    </div>\n                </div>\n            </div>        \n            <div class=\"logo-centro\">\n                <a href=\"#\" class=\"icono-boton\">\n                    <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/MundoF1.png\" alt=\"logo F1\">\n                </a>\n            </div>\n            <div class=\"botones-derecha\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CLASIFICACIÓN</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Pilotos</a>\n                        <a href=\"#\">Constructores</a>\n                    </div>\n                </div>\n                <div class=\"menu-desplegable\">    \n                    <button class=\"boton-header\">CALENDARIO</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Australia</a>\n                        <a href=\"#\">China</a>\n                        <a href=\"#\">Japón</a>\n                        <a href=\"#\">Bahrain</a>\n                        <a href=\"#\">Arabia Saudí</a>\n                        <a href=\"#\">Miami</a>\n                        <a href=\"#\">Imola</a>\n                        <a href=\"#\">Mónaco</a>\n                        <a href=\"#\">España</a>\n                        <a href=\"#\">Canada</a>\n                        <a href=\"#\">Austria</a>\n                        <a href=\"#\">Gran Bretaña</a>\n                        <a href=\"#\">Bélgica</a>\n                        <a href=\"#\">Hungría</a>\n                        <a href=\"#\">Holanda</a>\n                        <a href=\"#\">Italia</a>\n                        <a href=\"#\">Azerbayán</a>\n                        <a href=\"#\">Singapur</a>\n                        <a href=\"#\">Austin</a>\n                        <a href=\"#\">México</a>\n                        <a href=\"#\">Brasil</a>\n                        <a href=\"#\">Las Vegas</a>\n                        <a href=\"#\">Catar</a>\n                        <a href=\"#\">Abu Dhabi</a>\n                    </div>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"cuerpo\">\n        <div class=\"contenido-cuerpo\">\n            <h1>BIENVENIDO A MUNDO F1</h1>\n        </div>\n    </div>\n    \n    <section class=\"contenido-principal\">\n        <div class=\"contenedor-imagen-texto\">\n            <div class=\"imagen-izquierda\">\n                <img src=\"https://www.carlossainz.es/upload/media/press/0001/16/ca89fcd285389b6b348c979f0034ed929dc95581.jpeg\" alt=\"Carlos-Sainz\">\n            </div>\n            <div class=\"texto-contenido-derecha\">\n                <h2>DESCUBRE TODOS LOS DETALLES DEL GRAN CIRCO</h2>\n                <p>En <strong>Mundo F1</strong>, podrás encontrar toda la información, estadísticas y últimas noticias relacionadas con la máxima categoría de los deportes de motor. Aquí dispondrás de la información actualizada tras cada <strong>Gran Premio</strong> con todos los datos actualizados al minuto, sesión a sesión.</p>\n                <p>Explora nuestro sitio y descubre una nueva manera de disfrutar de tu <strong>deporte favorito</strong>.</p>\n            </div>\n        </div>    \n    </section>\n\n    <div class=\"contenedor-tarjetas-columnas\">\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://cdn.avpasion.com/wp-content/uploads/2024/03/sigue-gp-bahrein-formula-1-web-02.jpg\" alt=\"Tarjeta 1\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Live Timings</h3>\n            <p>No hay sesiones en directo.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://media.revistagq.com/photos/67e3e76c647fcc7e2803ca27/16:9/w_2560%2Cc_limit/2187699741\" alt=\"Tarjeta 2\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Últimas noticias</h3>\n            <p>Yuki Tsunoda, a Red Bull: Liam Lawson pilotará el RB a partir de Suzuka.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://s10.s3c.es/imag/_v0/5120x2794/b/8/3/BBPHDB.jpg\" alt=\"Tarjeta 3\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Próxima Carrera</h3>\n            <p>Toda la información sobre el GP de Japón.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n            <div class=\"tarjeta-imagen\">\n              <img src=\"https://imgresizer.eurosport.com/unsafe/1200x0/filters:format(jpeg)/origin-imgresizer.eurosport.com/2025/02/28/image-90710e0d-1722-4256-9cad-488a339e1c9b-85-2560-1440.jpeg\" alt=\"Tarjeta 3\" class=\"spectra-image\"/>\n            </div>\n            <div class=\"tarjeta-contenido\">\n              <h3>Pilotos españoles</h3>\n              <p>Últimas noticias sobre Fernando y Carlos Sainz.</p>\n              <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n            </div>\n          </div>\n  \n    </div>\n\n    <section class=\"pie-de-pagina\">\n        <div class=\"container-redes-sociales\">\n            <a href=\"#\" class=\"redes-sociales\">Instagram</a>\n            <a href=\"#\" class=\"redes-sociales\">Facebook</a>\n            <a href=\"#\" class=\"redes-sociales\">X</a>\n            <a href=\"#\" class=\"redes-sociales\">Tik Tok</a>\n            <a href=\"#\" class=\"redes-sociales\">Contacto</a>\n        </div>\n        <div class=\"texto-pie\">\n            <p>Esta página ha sido realizada por Pedro de la Rúa Lima. Derechos reservados.</p>\n        </div>\n    </section>\n</body>\n</html>\n<style>\n:root {\n    --color-primario: #ff0000;\n    --color-secundario: #292929;\n    --color-terciario: #ffffff;\n    --color-texto: #ffffff;\n    --color-texto-alt: #292929;\n}\n\nbody {\n    margin: 0;\n    font-family: \'Exo 2\', sans-serif;\n    background-image: url(\'https://png.pngtree.com/png-clipart/20240812/original/pngtree-pattern-lines-seamless-vector-png-image_15752243.png\');\n    background-repeat: no-repeat;\n    background-position: center center;\n    background-size: cover; /* o \'contain\' para ver toda la imagen */\n    background-attachment: fixed; /* opcional - para efecto parallax */\n    background-color: #ffffff; /* color de respaldo */\n}\n\n.barra-superior {\n    position:;\n    top: 0;\n    left: 0;\n    width: 100%;\n    max-width: 100vw;\n    padding: 10px 20px;\n    box-sizing: border-box;\n    justify-items: center;\n    z-index: 1000;\n    margin: 0;\n}\n\n.encabezado-superior {\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    border-radius: 30px;\n    width: 55%;\n    padding: 4px;\n    background-color: var(--color-primario);\n    box-sizing: border-box;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.botones-izquierda {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: right;\n}\n\n.botones-derecha {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: left;\n}\n\n.boton-header {\n    padding: 10px 20px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 15px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    display: inline-block;\n    background: none;\n    border: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n}\n\n.boton-header:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n}\n\n.icono-boton img {\n    height: 100px;\n    width: auto;\n    transition: transform 0.3s ease;\n}\n\n.icono-boton:hover img {\n    transform: scale(1.1);\n}\n\n.menu-desplegable {\n    position: relative;\n    display: inline-block;\n}\n\n.contenido-menus {\n    display: none;\n    position: absolute;\n    background-color: var(--color-terciario);\n    width: 100%;\n    min-width: max-content;\n    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);\n    z-index: 1;\n    border-radius: 0 0 15px 15px;\n    overflow: hidden;\n    top: 100%;\n    left: 0;\n}\n\n.contenido-menus a {\n    color: var(--color-texto-alt);\n    padding: 12px 16px;\n    text-decoration: none;\n    display: block;\n    font-size: 18px;\n    transition: background-color 0.3s;\n}\n\n.contenido-menus a:hover {\n    background-color: #f1f1f1;\n}\n\n.menu-desplegable:hover .contenido-menus {\n    display: block;\n}\n\n.menu-desplegable > .boton-header {\n    width: 100%;\n    box-sizing: border-box;\n    text-align: center;\n}\n\n.menu-desplegable:hover .boton-header {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    border-radius: 15px 15px 0 0;\n}\n\n.cuerpo {\n    margin-top: 50px;\n    height: 30vh;\n    background-image: url(\'https://www.formula1.com/content/dam/fom-website/manual/Misc/2022manual/WinterFebruary/GettyImages-1235524583.jpg\');\n    background-position-x: center;\n    background-position-y: -60vh;\n    background-size: cover;\n    width: 100%;\n    max-width: 100vw;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.cuerpo::before {\n    content: \'\';\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background-color: rgba(0, 0, 0, 0.5);\n}\n\n.contenido-cuerpo {\n    position: relative;\n    color: var(--color-texto);\n    text-align: center;\n    padding: 0 20px;\n}\n\n.contenido-cuerpo h1 {\n    font-size: 90px;\n    margin: 0;\n    text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);\n}\n\n.contenedor-imagen-texto {\n    display: grid;\n    grid-template-columns: 1fr 1fr;\n    align-items: start;\n    max-width: 1200px;\n    margin: 40px auto;\n    gap: 40px;\n    padding: 25px 50px;\n    background: linear-gradient(180deg, var(--color-terciario), #e7e7e7, var(--color-terciario));\n    border-radius: 30px;\n}\n\n.imagen-izquierda {\n    position: relative;\n    overflow: hidden;\n    border-radius: 15px;\n    box-shadow: 0 10px 20px rgba(0,0,0,0.15);\n    height: auto;\n    max-height: 400px;\n    aspect-ratio: 16/18;\n    display: flex;\n    align-items: center;\n}\n\n.imagen-izquierda img {\n    width: 100%;\n    height: auto;\n    object-fit: contain;\n    transition: transform 0.5s ease;\n}\n\n.texto-contenido-derecha {\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n    height: 100%;\n}\n\n.texto-contenido-derecha h2 {\n    color: #ff0000;\n    font-size: 2.2rem;\n    margin-bottom: 1.2rem;\n    line-height: 1.3;\n    position: relative;\n    padding-bottom: 12px;\n}\n\n.texto-contenido-derecha h2::after {\n    content: \'\';\n    position: absolute;\n    bottom: 0;\n    left: 0;\n    width: 60px;\n    height: 3px;\n    background-color: #ff0000;\n}\n\n.texto-contenido-derecha p {\n    font-size: 1rem;\n    line-height: 1.7;\n    color: #292929;\n    margin-bottom: 1.2rem;\n}\n\n.contenedor-tarjetas-columnas {\n    display: grid;\n    justify-content: center;\n    grid-template-columns: repeat(4, 300px);\n    gap: 75px;\n    margin: 50px 0;\n    padding-top: 50px;\n    width: 100%;\n}\n\n.tarjeta-estilo {\n    background: var(--color-primario);\n    border-radius: 30px;\n    overflow: hidden;\n    box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n    transition: transform 0.3s ease, box-shadow 0.3s ease;\n    display: flex;\n    flex-direction: column;\n    height: 100%;\n}\n\n.tarjeta-estilo:hover {\n    transform: translateY(-10px);\n    box-shadow: 0 15px 30px rgba(0,0,0,0.15);\n}\n\n.tarjeta-imagen {\n    height: 250px;\n    overflow: hidden;\n}\n\n.tarjeta-imagen img {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n    transition: transform 0.5s ease;\n}\n\n.tarjeta-estilo:hover .tarjeta-imagen img {\n    transform: scale(1.05);\n}\n\n  .tarjeta-contenido {\n    padding: 25px;\n    flex-grow: 1;\n    display: flex;\n    flex-direction: column;\n}\n\n.tarjeta-contenido h3 {\n    font-family: \'Exo 2\';\n    font-size: 1.8rem;\n    letter-spacing: 0.5px;\n    margin-bottom: 10px;\n    color: var(--color-texto);\n}\n\n.tarjeta-contenido p {\n    font-family: \'Exo 2\';\n    color: var(--color-texto);\n    margin-bottom: 20px;\n    line-height: 1.6;\n    flex-grow: 1;\n}\n\n.tarjeta-boton {\n    display: inline-block;\n    padding: 12px 25px;\n    background: var(--color-terciario);\n    color: var(--color-primario);\n    text-decoration: none;\n    border-radius: 30px;\n    font-family: \'Bebas Neue\', sans-serif;\n    font-size: 1.2rem;\n    font-weight: 400;\n    transition: all 0.3s ease;\n    text-align: center;\n    margin-top: auto;\n}\n\n.tarjeta-boton:hover {\n    transform: translateY(10px);\n}\n\n.pie-de-pagina {\n    display: flex;\n    flex-direction: column;\n    align-items: center;\n    justify-content: center;\n    padding: 20px 0px;\n    margin-top: 250px;\n    margin-bottom: 0;\n    background: linear-gradient(0deg, var(--color-secundario)25%, #303030, var(--color-secundario)75%);\n    height: 300px;\n    min-height: 100%;\n    width: 100%;\n    max-width: 100vw;\n    border-radius:0px;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.container-redes-sociales {\n    display: flex;\n    justify-content: center;\n    gap: 20px;\n    width: 80%;\n}\n\n.redes-sociales {\n    padding: 12px 24px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 30px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    background: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n    margin: 0;\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n}\n\n.redes-sociales:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    transform: translateY(-3px);\n}\n\n.texto-pie {\n    padding-top: 200px;\n    font-family: \'Exo 2\';\n    font-size: 12px;\n    color: var(--color-texto);\n}\n</style>\n<!-- /wp:html -->', '', '', 'inherit', 'closed', 'closed', '', '23-revision-v1', '', '', '2025-04-01 17:45:14', '2025-04-01 15:45:14', '', 23, 'http://localhost/wordpress/?p=25', 0, 'revision', '', 0),
(27, 2, '2025-04-03 09:35:58', '2025-04-03 07:35:58', '<!-- wp:group {\"tagName\":\"main\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|60\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<main class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--60)\"><!-- wp:query {\"queryId\":0,\"query\":{\"perPage\":10,\"pages\":0,\"offset\":0,\"postType\":\"post\",\"order\":\"desc\",\"orderBy\":\"date\",\"author\":\"\",\"search\":\"\",\"exclude\":[],\"sticky\":\"\",\"inherit\":true,\"taxQuery\":null,\"parents\":[]},\"align\":\"full\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-query alignfull\"><!-- wp:post-template {\"align\":\"full\",\"layout\":{\"type\":\"default\"}} -->\n<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|60\",\"bottom\":\"var:preset|spacing|60\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\" style=\"padding-top:var(--wp--preset--spacing--60);padding-bottom:var(--wp--preset--spacing--60)\"><!-- wp:post-content {\"align\":\"full\",\"fontSize\":\"medium\",\"layout\":{\"type\":\"constrained\"}} /--></div>\n<!-- /wp:group -->\n<!-- /wp:post-template --></div>\n<!-- /wp:query --></main>\n<!-- /wp:group -->', 'Inicio del blog', 'Muestra las entradas más recientes, así como la página de inicio del sitio o una página personalizada definida en los ajustes de lectura. Si existe, la plantilla de página de inicio sobreescribirá a esta plantilla cuando se muestren las entradas en dicha página de inicio.', 'publish', 'closed', 'closed', '', 'home', '', '', '2025-04-03 09:35:58', '2025-04-03 07:35:58', '', 0, 'http://localhost/wordpress/2025/04/03/home/', 0, 'wp_template', '', 0),
(28, 2, '2025-04-03 09:35:58', '2025-04-03 07:35:58', '<!-- wp:group {\"align\":\"full\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignfull\"></div>\n<!-- /wp:group -->', 'Cabecera', '', 'publish', 'closed', 'closed', '', 'header', '', '', '2025-04-03 09:35:58', '2025-04-03 07:35:58', '', 0, 'http://localhost/wordpress/2025/04/03/header/', 0, 'wp_template_part', '', 0),
(29, 2, '2025-04-03 09:35:58', '2025-04-03 07:35:58', '<!-- wp:group {\"tagName\":\"main\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|60\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<main class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--60)\"><!-- wp:query {\"queryId\":0,\"query\":{\"perPage\":10,\"pages\":0,\"offset\":0,\"postType\":\"post\",\"order\":\"desc\",\"orderBy\":\"date\",\"author\":\"\",\"search\":\"\",\"exclude\":[],\"sticky\":\"\",\"inherit\":true,\"taxQuery\":null,\"parents\":[]},\"align\":\"full\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-query alignfull\"><!-- wp:post-template {\"align\":\"full\",\"layout\":{\"type\":\"default\"}} -->\n<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|60\",\"bottom\":\"var:preset|spacing|60\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\" style=\"padding-top:var(--wp--preset--spacing--60);padding-bottom:var(--wp--preset--spacing--60)\"><!-- wp:post-content {\"align\":\"full\",\"fontSize\":\"medium\",\"layout\":{\"type\":\"constrained\"}} /--></div>\n<!-- /wp:group -->\n<!-- /wp:post-template --></div>\n<!-- /wp:query --></main>\n<!-- /wp:group -->', 'Inicio del blog', 'Muestra las entradas más recientes, así como la página de inicio del sitio o una página personalizada definida en los ajustes de lectura. Si existe, la plantilla de página de inicio sobreescribirá a esta plantilla cuando se muestren las entradas en dicha página de inicio.', 'inherit', 'closed', 'closed', '', '27-revision-v1', '', '', '2025-04-03 09:35:58', '2025-04-03 07:35:58', '', 27, 'http://localhost/wordpress/?p=29', 0, 'revision', '', 0),
(30, 2, '2025-04-03 09:39:16', '2025-04-03 07:39:16', '<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Fórmula 1</title>\n    <link rel=\"stylesheet\" href=\"HeaderConBotonesCSS.css\">\n    <link href=\"https://fonts.googleapis.com/css2?family=Exo+2:ital,wght@0,100..900;1,100..900&family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n</head>\n<body>\n    <div class=\"barra-superior\">\n        <div class=\"encabezado-superior\">\n            <div class=\"botones-izquierda\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">PILOTOS</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Verstappen</a>\n                        <a href=\"#\">Tsunoda</a>\n                        <a href=\"#\">Norris</a>\n                        <a href=\"#\">Piastri</a>\n                        <a href=\"#\">Leclerc</a>\n                        <a href=\"#\">Hamilton</a>\n                        <a href=\"#\">Russell</a>\n                        <a href=\"#\">Antonneli</a>\n                        <a href=\"#\">Alonso</a>\n                        <a href=\"#\">Stroll</a>\n                        <a href=\"#\">Gasly</a>\n                        <a href=\"#\">Doohan</a>\n                        <a href=\"#\">Ocon</a>\n                        <a href=\"#\">Bearman</a>\n                        <a href=\"#\">Lawson</a>\n                        <a href=\"#\">Hadjar</a>\n                        <a href=\"#\">Albon</a>\n                        <a href=\"#\">Sainz</a>\n                        <a href=\"#\">Hülkenberg</a>\n                        <a href=\"#\">Bortoleto</a>\n                    </div>\n                </div>\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CONSTRUCTORES</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">McLaren</a>\n                        <a href=\"#\">Ferrari</a>\n                        <a href=\"#\">Red Bull</a>\n                        <a href=\"#\">Mercedes</a>\n                        <a href=\"#\">Aston Martin</a>\n                        <a href=\"#\">Alpine</a>\n                        <a href=\"#\">Haas</a>\n                        <a href=\"#\">RB</a>\n                        <a href=\"#\">Williams</a>\n                        <a href=\"#\">Kick Sauber</a>\n                    </div>\n                </div>\n            </div>        \n            <div class=\"logo-centro\">\n                <a href=\"#\" class=\"icono-boton\">\n                    <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/MundoF1.png\" alt=\"logo F1\">\n                </a>\n            </div>\n            <div class=\"botones-derecha\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CLASIFICACIÓN</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Pilotos</a>\n                        <a href=\"#\">Constructores</a>\n                    </div>\n                </div>\n                <div class=\"menu-desplegable\">    \n                    <button class=\"boton-header\">CALENDARIO</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Australia</a>\n                        <a href=\"#\">China</a>\n                        <a href=\"#\">Japón</a>\n                        <a href=\"#\">Bahrain</a>\n                        <a href=\"#\">Arabia Saudí</a>\n                        <a href=\"#\">Miami</a>\n                        <a href=\"#\">Imola</a>\n                        <a href=\"#\">Mónaco</a>\n                        <a href=\"#\">España</a>\n                        <a href=\"#\">Canada</a>\n                        <a href=\"#\">Austria</a>\n                        <a href=\"#\">Gran Bretaña</a>\n                        <a href=\"#\">Bélgica</a>\n                        <a href=\"#\">Hungría</a>\n                        <a href=\"#\">Holanda</a>\n                        <a href=\"#\">Italia</a>\n                        <a href=\"#\">Azerbayán</a>\n                        <a href=\"#\">Singapur</a>\n                        <a href=\"#\">Austin</a>\n                        <a href=\"#\">México</a>\n                        <a href=\"#\">Brasil</a>\n                        <a href=\"#\">Las Vegas</a>\n                        <a href=\"#\">Catar</a>\n                        <a href=\"#\">Abu Dhabi</a>\n                    </div>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"cuerpo\">\n        <div class=\"contenido-cuerpo\">\n            <h1>BIENVENIDO A MUNDO F1</h1>\n        </div>\n    </div>\n    \n    <section class=\"contenido-principal\">\n        <div class=\"contenedor-imagen-texto\">\n            <div class=\"imagen-izquierda\">\n                <img src=\"https://www.carlossainz.es/upload/media/press/0001/16/ca89fcd285389b6b348c979f0034ed929dc95581.jpeg\" alt=\"Carlos-Sainz\">\n            </div>\n            <div class=\"texto-contenido-derecha\">\n                <h2>DESCUBRE TODOS LOS DETALLES DEL GRAN CIRCO</h2>\n                <p>En <strong>Mundo F1</strong>, podrás encontrar toda la información, estadísticas y últimas noticias relacionadas con la máxima categoría de los deportes de motor. Aquí dispondrás de la información actualizada tras cada <strong>Gran Premio</strong> con todos los datos actualizados al minuto, sesión a sesión.</p>\n                <p>Explora nuestro sitio y descubre una nueva manera de disfrutar de tu <strong>deporte favorito</strong>.</p>\n            </div>\n        </div>    \n    </section>\n\n    <div class=\"contenedor-tarjetas-columnas\">\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://cdn.avpasion.com/wp-content/uploads/2024/03/sigue-gp-bahrein-formula-1-web-02.jpg\" alt=\"Tarjeta 1\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Live Timings</h3>\n            <p>No hay sesiones en directo.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://media.revistagq.com/photos/67e3e76c647fcc7e2803ca27/16:9/w_2560%2Cc_limit/2187699741\" alt=\"Tarjeta 2\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Últimas noticias</h3>\n            <p>Yuki Tsunoda, a Red Bull: Liam Lawson pilotará el RB a partir de Suzuka.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n          <div class=\"tarjeta-imagen\">\n            <img src=\"https://s10.s3c.es/imag/_v0/5120x2794/b/8/3/BBPHDB.jpg\" alt=\"Tarjeta 3\" class=\"spectra-image\"/>\n          </div>\n          <div class=\"tarjeta-contenido\">\n            <h3>Próxima Carrera</h3>\n            <p>Toda la información sobre el GP de Japón.</p>\n            <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n          </div>\n        </div>\n        <div class=\"tarjeta-estilo\">\n            <div class=\"tarjeta-imagen\">\n              <img src=\"https://imgresizer.eurosport.com/unsafe/1200x0/filters:format(jpeg)/origin-imgresizer.eurosport.com/2025/02/28/image-90710e0d-1722-4256-9cad-488a339e1c9b-85-2560-1440.jpeg\" alt=\"Tarjeta 3\" class=\"spectra-image\"/>\n            </div>\n            <div class=\"tarjeta-contenido\">\n              <h3>Pilotos españoles</h3>\n              <p>Últimas noticias sobre Fernando y Carlos Sainz.</p>\n              <a href=\"#\" class=\"tarjeta-boton\">Ver más</a>\n            </div>\n          </div>\n  \n    </div>\n\n    <section class=\"pie-de-pagina\">\n        <div class=\"container-redes-sociales\">\n            <a href=\"#\" class=\"redes-sociales\">Instagram</a>\n            <a href=\"#\" class=\"redes-sociales\">Facebook</a>\n            <a href=\"#\" class=\"redes-sociales\">X</a>\n            <a href=\"#\" class=\"redes-sociales\">Tik Tok</a>\n            <a href=\"#\" class=\"redes-sociales\">Contacto</a>\n        </div>\n        <div class=\"texto-pie\">\n            <p>Esta página ha sido realizada por Pedro de la Rúa Lima. Derechos reservados.</p>\n        </div>\n    </section>\n</body>\n</html>\n<style>\n:root {\n    --color-primario: #ff0000;\n    --color-secundario: #292929;\n    --color-terciario: #ffffff;\n    --color-texto: #ffffff;\n    --color-texto-alt: #292929;\n}\n\nbody {\n    margin: 0;\n    font-family: \'Exo 2\', sans-serif;\n    background-image: url(\'https://png.pngtree.com/png-clipart/20240812/original/pngtree-pattern-lines-seamless-vector-png-image_15752243.png\');\n    background-repeat: no-repeat;\n    background-position: center center;\n    background-size: cover; /* o \'contain\' para ver toda la imagen */\n    background-attachment: fixed; /* opcional - para efecto parallax */\n    background-color: #ffffff; /* color de respaldo */\n}\n\n.barra-superior {\n    position:;\n    top: 0;\n    left: 0;\n    width: 100%;\n    max-width: 100vw;\n    padding: 10px 20px;\n    box-sizing: border-box;\n    justify-items: center;\n    z-index: 1000;\n    margin: 0;\n}\n\n.encabezado-superior {\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    border-radius: 30px;\n    width: 55%;\n    padding: 4px;\n    background-color: var(--color-primario);\n    box-sizing: border-box;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.botones-izquierda {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: right;\n}\n\n.botones-derecha {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: left;\n}\n\n.boton-header {\n    padding: 10px 20px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 15px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    display: inline-block;\n    background: none;\n    border: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n}\n\n.boton-header:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n}\n\n.icono-boton img {\n    height: 100px;\n    width: auto;\n    transition: transform 0.3s ease;\n}\n\n.icono-boton:hover img {\n    transform: scale(1.1);\n}\n\n.menu-desplegable {\n    position: relative;\n    display: inline-block;\n}\n\n.contenido-menus {\n    display: none;\n    position: absolute;\n    background-color: var(--color-terciario);\n    width: 100%;\n    min-width: max-content;\n    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);\n    z-index: 1;\n    border-radius: 0 0 15px 15px;\n    overflow: hidden;\n    top: 100%;\n    left: 0;\n}\n\n.contenido-menus a {\n    color: var(--color-texto-alt);\n    padding: 12px 16px;\n    text-decoration: none;\n    display: block;\n    font-size: 18px;\n    transition: background-color 0.3s;\n}\n\n.contenido-menus a:hover {\n    background-color: #f1f1f1;\n}\n\n.menu-desplegable:hover .contenido-menus {\n    display: block;\n}\n\n.menu-desplegable > .boton-header {\n    width: 100%;\n    box-sizing: border-box;\n    text-align: center;\n}\n\n.menu-desplegable:hover .boton-header {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    border-radius: 15px 15px 0 0;\n}\n\n.cuerpo {\n    margin-top: 50px;\n    height: 30vh;\n    background-image: url(\'https://www.formula1.com/content/dam/fom-website/manual/Misc/2022manual/WinterFebruary/GettyImages-1235524583.jpg\');\n    background-position-x: center;\n    background-position-y: -60vh;\n    background-size: cover;\n    width: 100%;\n    max-width: 100vw;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.cuerpo::before {\n    content: \'\';\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background-color: rgba(0, 0, 0, 0.5);\n}\n\n.contenido-cuerpo {\n    position: relative;\n    color: var(--color-texto);\n    text-align: center;\n    padding: 0 20px;\n}\n\n.contenido-cuerpo h1 {\n    font-size: 90px;\n    margin: 0;\n    text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);\n}\n\n.contenedor-imagen-texto {\n    display: grid;\n    grid-template-columns: 1fr 1fr;\n    align-items: start;\n    max-width: 1200px;\n    margin: 40px auto;\n    gap: 40px;\n    padding: 25px 50px;\n    background: linear-gradient(180deg, var(--color-terciario), #e7e7e7, var(--color-terciario));\n    border-radius: 30px;\n}\n\n.imagen-izquierda {\n    position: relative;\n    overflow: hidden;\n    border-radius: 15px;\n    box-shadow: 0 10px 20px rgba(0,0,0,0.15);\n    height: auto;\n    max-height: 400px;\n    aspect-ratio: 16/18;\n    display: flex;\n    align-items: center;\n}\n\n.imagen-izquierda img {\n    width: 100%;\n    height: auto;\n    object-fit: contain;\n    transition: transform 0.5s ease;\n}\n\n.texto-contenido-derecha {\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n    height: 100%;\n}\n\n.texto-contenido-derecha h2 {\n    color: #ff0000;\n    font-size: 2.2rem;\n    margin-bottom: 1.2rem;\n    line-height: 1.3;\n    position: relative;\n    padding-bottom: 12px;\n}\n\n.texto-contenido-derecha h2::after {\n    content: \'\';\n    position: absolute;\n    bottom: 0;\n    left: 0;\n    width: 60px;\n    height: 3px;\n    background-color: #ff0000;\n}\n\n.texto-contenido-derecha p {\n    font-size: 1rem;\n    line-height: 1.7;\n    color: #292929;\n    margin-bottom: 1.2rem;\n}\n\n.contenedor-tarjetas-columnas {\n    display: grid;\n    justify-content: center;\n    grid-template-columns: repeat(4, 300px);\n    gap: 75px;\n    margin: 50px 0;\n    padding-top: 50px;\n    width: 100%;\n}\n\n.tarjeta-estilo {\n    background: var(--color-primario);\n    border-radius: 30px;\n    overflow: hidden;\n    box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n    transition: transform 0.3s ease, box-shadow 0.3s ease;\n    display: flex;\n    flex-direction: column;\n    height: 100%;\n}\n\n.tarjeta-estilo:hover {\n    transform: translateY(-10px);\n    box-shadow: 0 15px 30px rgba(0,0,0,0.15);\n}\n\n.tarjeta-imagen {\n    height: 250px;\n    overflow: hidden;\n}\n\n.tarjeta-imagen img {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n    transition: transform 0.5s ease;\n}\n\n.tarjeta-estilo:hover .tarjeta-imagen img {\n    transform: scale(1.05);\n}\n\n  .tarjeta-contenido {\n    padding: 25px;\n    flex-grow: 1;\n    display: flex;\n    flex-direction: column;\n}\n\n.tarjeta-contenido h3 {\n    font-family: \'Exo 2\';\n    font-size: 1.8rem;\n    letter-spacing: 0.5px;\n    margin-bottom: 10px;\n    color: var(--color-texto);\n}\n\n.tarjeta-contenido p {\n    font-family: \'Exo 2\';\n    color: var(--color-texto);\n    margin-bottom: 20px;\n    line-height: 1.6;\n    flex-grow: 1;\n}\n\n.tarjeta-boton {\n    display: inline-block;\n    padding: 12px 25px;\n    background: var(--color-terciario);\n    color: var(--color-primario);\n    text-decoration: none;\n    border-radius: 30px;\n    font-family: \'Bebas Neue\', sans-serif;\n    font-size: 1.2rem;\n    font-weight: 400;\n    transition: all 0.3s ease;\n    text-align: center;\n    margin-top: auto;\n}\n\n.tarjeta-boton:hover {\n    transform: translateY(10px);\n}\n\n.pie-de-pagina {\n    display: flex;\n    flex-direction: column;\n    align-items: center;\n    justify-content: center;\n    padding: 20px 0px;\n    margin-top: 250px;\n    margin-bottom: 0;\n    background: linear-gradient(0deg, var(--color-secundario)25%, #303030, var(--color-secundario)75%);\n    height: 300px;\n    min-height: 100%;\n    width: 100%;\n    max-width: 100vw;\n    border-radius:0px;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.container-redes-sociales {\n    display: flex;\n    justify-content: center;\n    gap: 20px;\n    width: 80%;\n}\n\n.redes-sociales {\n    padding: 12px 24px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 30px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    background: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n    margin: 0;\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n}\n\n.redes-sociales:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    transform: translateY(-3px);\n}\n\n.texto-pie {\n    padding-top: 200px;\n    font-family: \'Exo 2\';\n    font-size: 12px;\n    color: var(--color-texto);\n}\n</style>\n<!-- /wp:html -->', 'Inicio - Mundo F1', '', 'inherit', 'closed', 'closed', '', '23-revision-v1', '', '', '2025-04-03 09:39:16', '2025-04-03 07:39:16', '', 23, 'http://localhost/wordpress/?p=30', 0, 'revision', '', 0),
