(36, 2, '2025-04-03 09:56:46', '2025-04-03 07:56:46', '<!-- wp:group {\"className\":\"site-footer\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"right\":\"0\"}},\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|foreground\"}}}},\"backgroundColor\":\"black\",\"textColor\":\"foreground\",\"layout\":{\"type\":\"default\"}} -->\r\n<div class=\"wp-block-group site-footer has-foreground-color has-black-background-color has-text-color has-background has-link-color\" style=\"padding-top:0;padding-right:0;padding-bottom:0;padding-left:0\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\",\"left\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\"}}},\"layout\":{\"type\":\"default\"}} -->\r\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--70);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--70);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:group {\"layout\":{\"type\":\"constrained\",\"contentSize\":\"1170px\"}} -->\r\n<div class=\"wp-block-group\"><!-- wp:columns {\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|70\"}}}} -->\r\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"25%\"} -->\r\n<div class=\"wp-block-column\" style=\"flex-basis:25%\"><!-- wp:heading {\"level\":3,\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|foreground\"}}},\"typography\":{\"fontSize\":\"26px\"},\"spacing\":{\"margin\":{\"bottom\":\"var:preset|spacing|60\"}}},\"textColor\":\"foreground\",\"fontFamily\":\"playfair-display\"} -->\r\n<h3 class=\"wp-block-heading has-foreground-color has-text-color has-link-color has-playfair-display-font-family\" style=\"margin-bottom:var(--wp--preset--spacing--60);font-size:26px\">About Info</h3>\r\n<!-- /wp:heading -->\r\n\r\n<!-- wp:paragraph {\"style\":{\"elements\":