: center;\n    gap: 20px;\n    width: 80%;\n}\n\n.redes-sociales {\n    padding: 12px 24px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 30px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    background: none;\n    cursor: pointer;\n    font-family: \'Exo 2\';\n    font-size: 1.2rem;\n    margin: 0;\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n}\n\n.redes-sociales:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    transform: translateY(-3px);\n}\n\n.texto-pie {\n    padding-top: 200px;\n    font-family: \'Exo 2\';\n    font-size: 12px;\n    color: var(--color-texto);\n}\n</style>\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Mundo F1</title>\n    <link rel=\"stylesheet\" href=\"HeaderConBotonesCSS.css\">\n    <link href=\"https://fonts.googleapis.com/css2?family=Exo+2:ital,wght@0,100..900;1,100..900&family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n</head>\n<body>\n    <section class=\"barra-superior\">\n        <div class=\"encabezado-superior\">\n            <div class=\"botones-izquierda\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">PILOTOS</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Verstappen</a>\n                        <a href=\"#\">Tsunoda</a>\n                        <a href=\"#\">Norris</a>\n                        <a href=\"#\">Piastri</a>\n                        <a href=\"#\">Leclerc</a>\n                        <a href=\"#\">Hamilton</a>\n                        <a href=\"#\">Russell</a>\n                        <a href=\"#\">Antonneli</a>\n                        <a href=\"#\">Alonso</a>\n                        <a href=\"#\">Stroll</a>\n                        <a href=\"#\">Gasly</a>\n                        <a href=\"#\">Doohan</a>\n                        <a href=\"#\">Ocon</a>\n                        <a href=\"#\">Bearman</a>\n                        <a href=\"#\">Lawson</a>\n                        <a href=\"#\">Hadjar</a>\n                        <a href=\"#\">Albon</a>\n                        <a href=\"#\">Sainz</a>\n                        <a href=\"#\">Hülkenberg</a>\n                        <a href=\"#\">Bortoleto</a>\n                    </div>\n                </div>\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CONSTRUCTORES</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">McLaren</a>\n                        <a href=\"#\">Ferrari</a>\n                        <a href=\"#\">Red Bull</a>\n                        <a href=\"#\">Mercedes</a>\n                        <a href=\"#\">Aston Martin</a>\n                        <a href=\"#\">Alpine</a>\n                        <a href=\"#\">Haas</a>\n                        <a href=\"#\">RB</a>\n                        <a href=\"#\">Williams</a>\n                        <a href=\"#\">Kick Sauber</a>\n                    </div>\n                </div>\n            </div>        \n            <div class=\"logo-centro\">\n                <a href=\"http://localhost/wordpress/\" class=\"icono-boton\">\n                    <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/MundoF1.png\" alt=\"logo F1\">\n                </a>\n            </div>\n            <div class=\"botones-derecha\">\n                <div class=\"menu-desplegable\">\n                    <button class=\"boton-header\">CLASIFICACIÓN</button>\n                    <div class=\"contenido-menus\">\n                        <a href=\"#\">Pilotos</a>\n                        <a href=\"#\">Constructores</a>\n                    </div>\n                </div>\n        