INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(131, 2, '2025-04-04 00:36:50', '2025-04-03 22:36:50', '<!-- wp:html -->\n<style>\n.body {\n    width: 100vw !important;\n    max-width: 100% !important;\n    margin: 0 !important;\n    padding: 0 !important;\n}\n\n.contenido-principal {\n    display: flex !important;\n    width: 100vw !important;\n    max-width: 100% !important;\n    margin: 0 !important;\n    padding: 20px !important;\n    justify-items: center !important;\n}\n\n.barra-superior,\n.cuerpo,\n.contenedor-tarjetas-columnas,\n.pie-de-pagina {\n    width: 100% !important;\n    max-width: 100% !important;\n    margin-left: 0 !important;\n    margin-right: 0 !important;\n    padding-left: 0 !important;\n    padding-right: 0 !important;\n}\n\n.encabezado-superior {\n    width: 60% !important;\n}\n\n    :root {\n    --color-primario: #ff0000;\n    --color-secundario: #292929;\n    --color-terciario: #ffffff;\n    --color-texto: #ffffff;\n    --color-texto-alt: #292929;\n}\n\nbody {\n    margin: 0;\n    font-family: \'Exo 2\';\n    background-image: url(\'https://png.pngtree.com/png-clipart/20240812/original/pngtree-pattern-lines-seamless-vector-png-image_15752243.png\'); \n    background-position: center;\n    background-size: cover;\n    background-attachment: fixed;\n}\n\n.barra-superior {\n    position:;\n    top: 0;\n    left: 0;\n    width: 100%;\n    height: 100%;\n    max-width: 100vw;\n    padding: 10px 20px;\n    box-sizing: border-box;\n    justify-items: center;\n    z-index: 1000;\n    margin: 0;\n}\n\n.encabezado-superior {\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    border-radius: 30px;\n    width: 55%;\n    padding: 4px;\n    background-color: var(--color-primario);\n    box-sizing: border-box;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.botones-izquierda {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: right;\n}\n\n.botones-derecha {\n    display: flex;\n    font-size: 25px;\n    gap: 10px;\n    flex: 1;\n    justify-content: left;\n}\n\n.boton-header {\n    padding: 10px 20px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 15px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    display: inline-block;\n    background: none;\n    border: none;\n    cursor: pointer;\n    font-family: inherit;\n    font-size: inherit;\n}\n\n.boton-header:hover {\n    background-color: var(--color-texto);\n    color: var(--color-texto-alt);\n}\n\n.icono-boton img {\n    height: 100px;\n    width: auto;\n    transition: transform 0.3s ease;\n}\n\n.icono-boton:hover img {\n    transform: scale(1.1);\n}\n\n.menu-desplegable {\n    position: relative;\n    display: inline-block;\n}\n\n.contenido-menus {\n    display: none;\n    position: absolute;\n    background-color: var(--color-terciario);\n    width: 100%;\n    min-width: max-content;\n    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);\n    z-index: 1;\n    border-radius: 0 0 15px 15px;\n    overflow: hidden;\n    top: 100%;\n    left: 0;\n}\n\n.contenido-menus a {\n    color: var(--color-texto-alt);\n    padding: 12px 16px;\n    text-decoration: none;\n    display: block;\n    font-size: 18px;\n    transition: background-color 0.3s;\n}\n\n.contenido-menus a:hover {\n    background-color: #f1f1f1;\n}\n\n.menu-desplegable:hover .contenido-menus {\n    display: block;\n}\n\n.menu-desplegable > .boton-header {\n    width: 100%;\n    box-sizing: border-box;\n    text-align: center;\n}\n\n.menu-desplegable:hover .boton-header {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    border-radius: 15px 15px 0 0;\n}\n\n.cuerpo {\n    margin-bottom: 40px;\n    margin-top: 50px;\n    height: 40vh;\n    background-image: url(\'http://localhost/wordpress/wp-content/uploads/2025/04/max-verstappen-f1-3440x1440-13972-2.jpeg\');\n    background-position-x: center;\n    background-position-y: -10vh;\n    background-size: cover;\n    height: 300px;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n    border-radius: 50px;\n}\n\n.cuerpo::before {\n    content: \'\';\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background-color: rgba(0, 0, 0, 0.5);\n    border-radius: 50px;\n}\n\n.contenido-cuerpo {\n    position: relative;\n    color: var(--color-texto);\n    text-align: center;\n    padding: 0 20px;\n}\n\n.contenido-cuerpo h1 {\n    font-size: 90px;\n    margin: 0;\n    text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);\n}\n\n.contenedor-principal {\n    width: 100%;\n    max-width: 100vw;\n    margin: auto;\n    padding: 20px 50px;\n    box-sizing: border-box;\n}\n\n.contenedor-imagen-texto {\n    display: grid;\n    grid-template-columns: 1fr 1fr;\n    align-items: start;\n    justify-items: center;\n    width: 100%;\n    max-width: 1500px;\n    margin: auto;\n    gap: 50px;\n    padding: 50px 50px;\n    background: linear-gradient(180deg, #000125, #000133, #000125);\n    border-radius: 50px;\n    box-sizing: border-box;\n}\n\n.imagen-izquierda {\n    position: relative;\n    overflow: hidden;\n    border-radius: 50px;\n    margin-left: 20px;\n    margin-top: 50px;\n    height: auto;\n    max-height: 400px;\n    aspect-ratio:;\n    display: flex;\n    align-items: center;\n    box-shadow: 0px 0px 30px rgba(255, 228, 78, 0.5);\n}\n\n.imagen-izquierda img {\n    width: 100%;\n    height: 100%;\n    object-fit: contain;\n    transition: transform 0.5s ease;\n}\n\n.texto-contenido-derecha {\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n    height: 100%;\n}\n\n.texto-contenido-derecha h2 {\n    color: #f3d552;\n    font-size: 40px;\n    text-align: left;\n    text-justify: auto;\n    margin-bottom: 15px;\n    line-height: 1.3;\n    position: relative;\n    padding-bottom: 12px;\n}\n\n.texto-contenido-derecha p {\n    font-size: 20px;\n    line-height: 1.3;\n    color: var(--color-texto);\n    margin-bottom: 1.2rem;\n}\n\n.palmares {\n    color: var(--color-texto);\n    list-style-type: none;\n    align-text: left;\n    justify-content: left;\n}\n \n.contenedor-tarjetas-columnas {\n    display: grid;\n    justify-content: center;\n    grid-template-columns: repeat(4, 300px);\n    gap: 75px;\n    margin: 100px 0;\n    translateX: 30px;\n    padding-top: 100px;\n    width: 100%;\n}\n\n.tarjeta-estilo {\n    background: #000125;\n    border-radius: 30px;\n    overflow: hidden;\n    box-shadow: 0px 0px 15px rgba(0,0,0,0.7);\n    transition: transform 0.3s ease, box-shadow 0.3s ease;\n    display: flex;\n    flex-direction: column;\n    height: 100%;\n}\n\n.tarjeta-estilo:hover {\n    transform: translateY(-10px);\n    box-shadow: 0 15px 30px rgba(0,0,0,0.5);\n}\n\n.tarjeta-imagen {\n    height: 250px;\n    overflow: hidden;\n}\n\n.tarjeta-imagen img {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n    transition: transform 0.5s ease;\n}\n\n.tarjeta-estilo:hover .tarjeta-imagen img {\n    transform: scale(1.05);\n}\n\n.tarjeta-contenido {\n    padding: 20px 20px;\n    flex-grow: 1;\n    display: flex;\n    flex-direction: column;\n}\n\n.tarjeta-contenido h3 {\n    font-family: \'Exo 2\';\n    text-align: center;\n    font-size: 30px;\n    letter-spacing: 0.5px;\n    margin-bottom: 20px;\n    color: var(--color-texto);\n}\n\n.tarjeta-contenido p {\n    font-family: \'Exo 2\';\n    text-align: center;\n    font-size: 15px;\n    color: var(--color-texto);\n    margin-bottom: 20px;\n    line-height: 1.6;\n    flex-grow: 1;\n}\n\n.tarjeta-boton {\n    display: inline-block;\n    padding: 12px 25px;\n    background: var(--color-terciario);\n    color: var(--color-texto-alt);\n    text-decoration: none;\n    border-radius: 30px;\n    font-family: \'Exo 2\';\n    font-size: 1.2rem;\n    font-weight: 600;\n    transition: all 0.3s ease;\n    text-align: center;\n    margin