ize: cover;\n    height: 300px;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    position: relative;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n    border-radius: 50px;\n}\n\n.cuerpo::before {\n    content: \'\';\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background-color: rgba(0, 0, 0, 0.5);\n    border-radius: 50px;\n}\n\n.contenido-cuerpo {\n    position: relative;\n    color: var(--color-texto);\n    text-align: center;\n    padding: 0 20px;\n}\n\n.contenido-cuerpo h1 {\n    font-size: 90px;\n    margin: 0;\n    text-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);\n}\n\n.contenedor-principal {\n    width: 100%;\n    max-width: 100vw;\n    margin: auto;\n    padding: 20px 50px;\n    box-sizing: border-box;\n}\n\n.contenedor-imagen-texto {\n    display: grid;\n    grid-template-columns: 1fr 1fr;\n    align-items: start;\n    justify-items: center;\n    width: 100%;\n    max-width: 1500px;\n    margin: auto;\n    gap: 50px;\n    padding: 50px 50px;\n    background: linear-gradient(180deg, #000125, #000133, #000125);\n    border-radius: 50px;\n    box-sizing: border-box;\n}\n\n.imagen-izquierda {\n    position: relative;\n    overflow: hidden;\n    border-radius: 50px;\n    margin-left: 20px;\n    margin-top: 50px;\n    height: auto;\n    max-height: 400px;\n    aspect-ratio:;\n    display: flex;\n    align-items: center;\n    box-shadow: 0px 0px 30px rgba(255, 228, 78, 0.5);\n}\n\n.imagen-izquierda img {\n    width: 100%;\n    height: 100%;\n    object-fit: contain;\n    transition: transform 0.5s ease;\n}\n\n.texto-contenido-derecha {\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n    height: 100%;\n}\n\n.texto-contenido-derecha h2 {\n    color: #f3d552;\n    font-size: 40px;\n    text-align: left;\n    text-justify: auto;\n    margin-bottom: 15px;\n    line-height: 1.3;\n    position: relative;\n    padding-bottom: 12px;\n}\n\n.texto-contenido-derecha p {