-top: auto;\n}\n\n.tarjeta-boton:hover {\n    transform: translateY(10px);\n    background: #ffd000;\n    color: var(--color-texto-alt);\n}\n\n.pie-de-pagina {\n    display: flex;\n    flex-direction: column;\n    align-items: center;\n    justify-content: center;\n    padding: 20px 0px;\n    margin-top: 250px;\n    margin-bottom: 0;\n    background: linear-gradient(0deg, var(--color-secundario)25%, #303030, var(--color-secundario)75%);\n    height: 300px;\n    min-height: 100%;\n    width: 100%;\n    max-width: 100vw;\n    border-radius: 50px;\n    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);\n}\n\n.container-redes-sociales {\n    display: flex;\n    justify-content: center;\n    gap: 20px;\n    width: 80%;\n}\n\n.redes-sociales {\n    padding: 12px 24px;\n    color: var(--color-texto);\n    text-decoration: none;\n    font-weight: 600;\n    border-radius: 30px;\n    transition: all 0.3s ease;\n    white-space: nowrap;\n    background: none;\n    cursor: pointer;\n    font-family: \'Exo 2\';\n    font-size: 1.2rem;\n    margin: 0;\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n}\n\n.redes-sociales:hover {\n    background-color: var(--color-texto);\n    color: var(--color-primario);\n    transform: translateY(-3px);\n}\n\n.texto-pie {\n    padding-top: 200px;\n    font-family: \'Exo 2\';\n    font-size: 12px;\n    color: var(--color-texto);\n}\n</style>\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Mundo F1</title>\n    <link rel=\"stylesheet\" href=\"HeaderConBotonesCSS.css\">\n    <link href=\"https://fonts.googleapis.com/css2?family=Exo+2:ital,wght@0,100..900;1,100..900&family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n</head>\n<body>\n    <section class=\"barra-superior\">\n        <div class=\"encabezado-superior\">\n            <div class=\"botones-izquierda\">\n       