eet\">\n\n<style>\n    .slider-wrapper {\n        display: flex;\n        align-items: center;\n        justify-content: center;\n        gap: 10px;\n        max-width: calc(2 * 360px + 40px);\n        margin: auto;\n        padding: 20px 10px;\n        box-sizing: border-box;\n    }\n\n    .slider-container {\n        overflow: hidden;\n        flex: 1;\n    }\n\n    .slider {\n        display: flex;\n        transition: transform 0.5s ease;\n    }\n\n    .review {\n        font-family: \'Roboto\', sans-serif;\n        width: 310px;\n        padding: 15px;\n        margin-right: 20px;\n        background: #e2e2e2;\n        border-radius: 10px;\n        text-align: center;\n        box-sizing: border-box;\n        flex-shrink: 0;\n    }\n\n    .review:last-child {\n        margin-right: 0;\n    }\n\n    .arrow {\n        background: #00305e;\n        color: white;\n        border: none;\n        width: 40px;\n        height: 40px;\n        border-radius: 50%;\n        font-size: 18px;\n        box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);\n        cursor: pointer;\n        display: flex;\n        align-items: center;\n        justify-content: center;\n    }\n\n    @media (max-width: 700px) {\n        .slider-wrapper {\n            flex-direction: column;\n        }\n\n        .slider-container {\n            width: 100%;\n        }\n\n        .review {\n            width: 100%;\n            margin-right: 0;\n        }\n\n        .arrow {\n            margin: 10px 0;\n        }\n    }\n    \n    .arrow-group {\n        display: none;\n        gap: 10px;\n        margin-top: 15px;\n    }\n\n    .arrow.desktop {\n        display: flex;\n    }\n\n    @media (max-width: 700px) {\n        .arrow.desktop {\n            display: none;\n        }\n\n        .arrow-group.mobile {\n            display: flex;\n            justify-content: center;\n        }\n\n        .slider-wrapper {\n            flex-direction: column;\n            align-items: center;\n        }\n\n        .review {\n            width: 100%;\n            margin-right: 0;\n        }\n\n        .slider-container {\n            width: 100%;\n        }\n    }\n</style>\n\n<div class=\"slider-wrapper\">\n    <button class=\"arrow left desktop\" onclick=\"moveSlide(-1)\">\n        <svg width=\"20\" height=\"20\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"white\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\">\n            <polyline points=\"15 18 9 12 15 6\"></polyline>\n        </svg>\n    </button>\n\n    <div class=\"slider-container\">\n        <div class=\"slider\" id=\"slider\">\n            <!-- Todas las reseñas aquí -->\n        </div>\n    </div>\n\n    <button class=\"arrow right desktop\" onclick=\"moveSlide(1)\">\n        <svg width=\"20\" height=\"20\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"white\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\">\n            <polyline points=\"9 18 15 12 9 6\"></polyline>\n        </svg>\n    </button>\n</div>\n\n<div class=\"arrow-group mobile\">\n    <button class=\"arrow\" onclick=\"moveSlide(-1)\">\n        <svg width=\"20\" height=\"20\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"white\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\">\n            <polyline points=\"15 18 9 12 15 6\"></polyline>\n        </svg>\n    </button>\n    <button class=\"arrow\" onclick=\"moveSlide(1)\">\n        <svg width=\"20\" height=\"20\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"white\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\">\n            <polyline points=\"9 18 15 12 9 6\"></polyline>\n        </svg>\n    </button>\n</div>\n\n<script>\nfunction moveSlide(direction) {\n    const slider = document.getElementById(\"slider\");\n    const slideWidth = slider.children[0].offsetWidth + 20; // width + margin\n    slider.scrollBy({\n        left: direction * slideWidth,\n        behavior: \"smooth\"\n    });\n}\n</script>\n\n<!-- /wp:html -