adding: 100px 0px 0px;\n      margin: 0px -10px;\n      width: 100vw;\n      max-width: 100vw;\n\n    }\n\n    .contenedor-tarjetas-3columnas {\n      display: flex;\n      background-color: var(--color-suave);\n      font-family: \'Audiowide\', sans-serif;\n      justify-content: center;\n      flex-wrap: nowrap;\n      gap: 80px;\n      margin: -50px -10px;\n      padding: 200px 0px 300px;\n      width: 100vw;\n      max-width: 100vw;\n    }\n    .tarjeta-estilo {\n      background: #2ebdc2;\n      border-radius: 5px;\n      box-shadow: 0px 0px 15px rgb(0, 0, 0, 0.35);\n      display: flex;\n      flex-direction: column;\n      height: 70%;\n      width: calc(25% - 20px);\n      max-width: calc(25% - 20px);\n      min-width: 250px;\n    }\n    \n    .tarjeta-imagen {\n      height: 200px;\n      overflow: hidden;\n      border-radius: 5px;\n      border-bottom-left-radius: 0px;\n      border-bottom-right-radius: 0px;\n    }\n    .tarjeta-imagen img {\n      width: 100%;\n      height: 100%;\n      object-fit: cover;\n    \n    }\n    .tarjeta-estilo:hover .tarjeta-contenido {\n      transform: scale(1.05);\n      background-color: #00305e;\n      box-shadow: 0px 0px 15px rgb(0, 0, 0, 0.35);\n      border-radius: 5px;\n      border-top-right-radius: 0px;\n      border-top-left-radius: 0px;\n    }\n    .tarjeta-contenido {\n      padding: 15px;\n      flex-grow: 1;\n      display: flex;\n      flex-direction: column;\n      transition: transform 0.5s ease;\n    }\n    .tarjeta-contenido h3 {\n      text-align: center;\n      justify-content: center;\n      font-size: 1.5rem;\n      font-family: \'Audiowide\', sans-serif;\n      letter-spacing: 0.5px;\n      margin-bottom: 20px;\n      color: white;\n    }\n    .planes {\n      text-align: center;\n      text-decoration: none;\n      justify-content: center;\n      font-size: 1.5rem;\n      font-weight: 600;\n      font-family: \'Audiowide\', sans-serif;\n      letter-spacing: 0.5px;\n      margin-bottom: 20px;\n      margin-top: 20px;\n      color: white;\n    }\n    .card-header {\n      display: flex;\n      justify-content: center;\n      align-items: center;\n      height: 100px;\n      background-color: #2abec2;\n    }\n    .highlight {\n      background-color: white;\n      color: #2abec2;\n      padding: 6px 12px;\n      border-radius: 6px;\n      font-weight: bold;\n      font-size: 16px;\n    }\n    \n    .tarjeta-estilo:hover .card-header {\n      background-color: #00305e;\n    }\n    .tarjeta-contenido ul {\n      text-align: left;\n      padding-left: 20px; \n  }\n    .tarjeta-contenido {\n      font-size: 0.9rem; /* Reduce el tamaño del contenido de la tarjeta  */\n  }\n\n    .tarjeta-contenido h2 {\n      font-size: 1.1rem; /* Título más pequeño */\n  }\n\n    .tarjeta-contenido ul {\n      font-size: 0.9rem; /* Tamaño de lista pequeña */\n  }\n    .contenedor-tarjetas-3columnas {\n      align-items: stretch;\n  }\n\n\n\n\n\n\n\n    </style>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<style>\n        .texto-mapa {\n        font-family: \'Audiowide\', sans-serif;\n        font-size: 2.5rem;\n        text-align: center;\n        justify-content: center;\n        color: var(--color-caracter);\n        margin-top: -50px;\n      }\n\n  .mapa-sinergym-container {\n    width: 100%;\n    max-width: 100vw;\n    padding-bottom: 200px;\n    background-color: var(--color-suave);\n    box-sizing: border-box;\n    overflow: hidden;\n  }\n  .mapa-sinergym-container iframe {\n    width: 50%;\n    height: 500px;\n    border: none;\n    border-radius: 15px;\n    display: block;\n    margin: 0 auto;\n  }\n</style>\n\n<div class=\"mapa-sinergym-container\">\n  <div class=\"texto-mapa\">\n    <h2>DÓNDE ENCONTRARNOS</h2>\n  </div>\n  <iframe\n    src=\"https://www.google.com/maps?q=sinergym+espana&hl=es&ie=UTF8&t=&z=6&iwloc=B&output=embed\"\n    allowfullscreen\n    loading=\"lazy\"\n    referrerpolicy=\"no-referrer-when-downgrade\">\n  </iframe>\n</div>\n<!-- /wp:html -->\n\n<!-- wp:ht