: 100%;\n  height: 100%;\n  border: none;\n}\n\n.wp-home-button {\n  position: absolute;\n  bottom: 20px;\n  left: 50%;\n  transform: translateX(-50%);\n  width: 70px;\n  height: 6px;\n  background: #444;\n  border-radius: 3px;\n}\n\n  \n.contenido-central {\n  flex: 1;\n  max-width: 600px;\n  padding: 0 20px;\n  color: #00305e;\n  display: flex;\n  flex-direction: column;\n  justify-content: center;\n  text-align: justify;\n}\n  \n    .contenido-central h3 {\n      font-size: 40px;\n      font-family: \'Audiowide\', sans-serif;\n      margin-bottom: 30px;\n      text-align: center;\n    }\n  \n    .contenido-central h4 {\n      font-size: 28px;\n      margin-top: 40px;\n      margin-bottom: 25px;\n      text-align: center;\n  \n    }\n  \n    .contenido-central p {\n      font-size: 20px;\n      margin-bottom: 25px;\n      line-height: 1.6;\n    }\n  \n    .contenido-central ul {\n      list-style: disc;\n      padding-left: 1.8rem;\n      font-size: 22px;\n      margin-bottom: 40px;\n      color: black;\n    }\n  \n    .contenido-central li {\n      margin-bottom: 20px;\n    }\n    @media (max-width: 768px) {\n  .bloque-entrenamiento {\n    flex-direction: column;\n    align-items: center;\n    gap: 40px;\n    padding: 40px 20px;\n  }\n   \n  \n      .contenido-central {\n        text-align: center;\n        padding: 0;\n      }\n  \n      .contenido-central h3,\n      .contenido-central h4,\n      .contenido-central p,\n      .contenido-central ul {\n        text-align: justify;\n      }\n       h3 {\n        text-align: justify;\n       }\n       p {\n        text-align: justify;\n       }\n    }\n   \n\n    .bloque-resumen {\n  width: 100%;\n  max-width: 1200px;\n  margin: 0 auto;\n  padding: 40px 20px;\n  text-align: center;\n  background-color: #dff;\n}\n\n.bloque-resumen h5 {\n  font-size: 25px;\n  color: #00305e;\n  font-weight: normal;\n}\n  </style>\n  <div class=\"bloque-entrenamiento\">\n    <!-- iPad -->\n    <div class=\"planes-movil\">\n      <div cl