(128, 1, '2025-05-08 18:04:31', '2025-05-08 16:04:31', '<!-- wp:html -->\n<!-- gif de precarga de la página -->\n<div id=\"preloader\">\n  <div class=\"arm-loader\">\n    <img src=\"http://localhost/wordpress/wp-content/uploads/2025/05/weight-lifting-9170_256.gif\" alt=\"Cargando...\" />\n  </div>\n</div>\n\n<style>\n#preloader {\n  position: fixed;\n  width: 100%;\n  height: 100%;\n  background-color: #fff;\n  z-index: 9999;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  transition: opacity 0.5s ease;\n  opacity: 1;\n}\n\n.arm-loader img {\n  width: 150px;\n  animation: lift 1.5s ease-in-out infinite;\n}\n\n@keyframes lift {\n  0%, 100% {\n    transform: translateY(0) rotate(0deg);\n  }\n  50% {\n    transform: translateY(-20px) rotate(-10deg);\n  }\n}\n</style>\n\n<script>\nwindow.addEventListener(\"load\", function () {\n  const preloader = document.getElementById(\"preloader\");\n  if (preloader) {\n    preloader.style.opacity = \'0\';\n    preloader.style.pointerEvents = \'none\';\n    setTimeout(() => preloader.remove(), 500);\n  }\n});\n</script>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<style>\n  :root {\n  --color-primario: #2ebdc2;\n  --color-secundario: #ffffff;\n  --color-terciario: #202020;\n  --color-caracter: #00305e;\n  --color-texto: #e0ffff;\n  --color-texto-alt: #202020;\n  }\n\n  html {\n    scroll-behavior: smooth;\n  }\n  \n  .barra-superior {\n  font-family: \'Audiowide\', sans-serif;\n  position: sticky;\n  top: 0;\n  width: 100%;\n  height: 150px;\n  max-width: 100vw;\n  padding: 0px 20px;\n  box-sizing: border-box;\n  justify-items: center;\n  z-index: 1000;\n  margin: 0;\n  margin-top: -10px;    \n  }\n  \n  .encabezado-superior {\n      display: flex;\n      justify-content: space-between;\n      align-items: center;\n      border-radius: 0px;\n      width: 100vw;\n      margin-top: 0px;\n      padding: 5px;\n      background-color: var(--color-primario);\n      box-sizing: border-box;\n      position: relative;\n  }\n  \n  .botones-izquierda {\n      display: flex;\n      font-size: 25px;\n      gap: 10px;\n      flex: 1;\n      justify-content: right;\n  }\n  \n  .botones-derecha {\n      display: flex;\n      font-size: 25px;\n      gap: 10px;\n      flex: 1;\n      justify-content: left;\n  }\n  \n  .boton-header {\n      padding: 10px 15px;\n      margin-left: 10px;\n      margin-right: 10px;\n      color: var(--color-secundario);\n      text-decoration: none;\n      font-weight: 600;\n      border-radius: 30px;\n      transition: all 0.3s ease;\n      white-space: nowrap;\n      display: inline-block;\n      background: none;\n      border: none;\n      cursor: pointer;\n      font-family: inherit;\n      font-size: inherit;\n  }\n  \n  .boton-header:hover {\n      color: var(--color-caracter);\n      transform: translateY(-1px);\n  \n  }\n  \n  .icono-boton img {\n      padding: 10px;\n      height: 100px;\n      width: auto;\n      transition: transform 0.3s ease;\n  }\n  \n  .icono-boton:hover img {\n      transform: scale(1.0);\n  }\n  </style>\n  \n  <!DOCTYPE html>\n  <html lang=\"es\">\n  <head>\n      <meta charset=\"UTF-8\">\n      <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n      <title>Trainer</title>\n      <link href=\"https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n  </head>\n  <body>\n<section class=\"barra-superior\">\n  <div class=\"encabezado-superior\">\n      <div class=\"botones-izquierda\">\n          <div class=\"menu-desplegable\">\n              <a href=\"http://localhost/wordpress/inicio/\" class=\"boton-header\">INICIO</a>\n          </div>\n          <div class=\"menu-desplegable\">\n              <a href=\"#planes\" class=\"boton-header\">NUESTROS PLANES</a>\n          </div>\n      </div>        \n      <div class=\"logo-centro\">\n          <a href=\"#\" class=\"icono-boton\">\n              <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/logo-2.png\" alt=\"logo F1\">\n          </a>\n      </div>\n      <div class=\"botones-derecha\">\n          <div class=\"menu-desplegable\">\n              <a href=\"http://localhost/wordpress/quienes-somos/\" class=\"boton-header\">QUIENES SOMOS</a>\n          </div>\n          <div class=\"menu-desplegable\">    \n              <a href=\"#\" class=\"boton-header\">CONTACTO</a>\n          </div>\n      </div>\n  </div>\n</section>\n  </body>\n  </html>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<link href=\"https://fonts.googleapis.com/css2?family=Merienda&display=swap\" rel=\"stylesheet\">\n<link href=\"https://fonts.googleapis.com/css2?family=Audiowide&display=swap\" rel=\"stylesheet\">\n<link href=\"https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n  <div class=\"bloque-servicios\">\n    <div class=\"contenedor-interno\">\n      <div class=\"contenido-imagen\">\n        <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Entrenador-personal-online-768x945.sin_.fondo-removebg-preview.png\" alt=\"Nuestros servicios\" />\n      </div>\n      <div class=\"contenido-texto\">\n        <div class=\"frase-fondo\">ponte en forma</div>\n        <h3>TE AYUDAMOS A CUIDARTE</h3>\n        <h2><b>Nuestros servicios</b></h2>\n        <p>Ayudamos a todas las mujeres de cualquier edad a mejorar su salud y sus hábitos mediante planes 100% personalizados de dietas y entrenos.</p>\n      </div>\n    </div>\n  </div>\n  \n  <style>\n    .bloque-servicios {\n      position: relative;\n      margin-top: -50px;\n      margin-bottom: 200px;\n      width: 100%;\n      margin-left: -45px;\n      max-width: 100vw;\n      padding: 100px 30px 0px;\n      font-family: \'Roboto\';\n      background-repeat: no-repeat;\n      overflow: hidden;\n      background-color: #e0ffff;\n      text-align: center;\n      justify-content: center;\n    }\n  \n    .bloque-servicios::before {\n      content: \"\";\n      position: absolute;\n      top: 0;\n      left: 0;\n      width: 100%;\n      height: 100%;\n      background-color: #e0ffff;\n      z-index: 1;\n    }\n  \n    .contenedor-interno {\n      display: flex;\n      align-items: center;\n      justify-content: center;\n      gap: 40px;\n      position: relative;\n      z-index: 2;\n      flex-wrap: wrap; \n    }\n  \n    .contenido-texto {\n      max-width: 600px;\n      text-align: left;\n      padding-right: 40px;\n    }\n  \n    .contenido-imagen {\n      margin-right: 40px;\n    }\n    \n    .contenido-imagen img {\n      max-width: 370px;\n      width: 100%;\n      border-radius: 20px;\n      margin-right: 20px;\n    }\n  \n    .bloque-servicios h2 {\n      font-size: 2rem;\n      margin-bottom: 8px;\n      color: black;\n      text-align: center;\n      font-family: \'Roboto\';\n     \n    }\n  \n    .bloque-servicios h3 {\n      font-size: 3.3rem;\n      margin-bottom: 10px;\n      color: #2ebdc2;\n      text-align: center;\n      font-family: \'Audiowide\', sans-serif;\n    }\n  \n    .bloque-servicios p {\n      font-size: 20px;\n      margin: 0 auto;\n      color: black;\n      text-align: center;\n      font-family: \'Roboto\';\n\n    }\n  \n    .frase-fondo {\n      position: absolute;\n      top: 50%;\n      left: 64%;\n      transform: translate(-50%, -50%);\n      font-size: 8rem;\n      font-weight: bold;\n      font-family: \'Merienda\', cursive;\n      color: rgba(150, 207, 203, 0.4);\n      white-space: nowrap;\n      z-index: -1;\n      pointer-events: none;\n      user-select: none;\n    }\n  </style>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<style>\n  \n      .fondo-portatil {\n        background-color: var(--color-primario);\n        padding: 200px 10px 0px;\n        margin-top: -15%;\n        margin-bottom: 200px;\n        margin-left: -10px;\n        max-width: 100vw;\n        width: 100%;\n      }\n  \n      .texto-portatil {\n        font-family: \'Audiowide\', sans-serif;\n        font-size: 2.5rem;\n        text-align: center;\n        justify-content: center;\n        color: var(--color-texto);\n        margin-bottom: -20px;\n      }\n  \n      .portatil-container {\n        position: relative;\n        width: 600px;\n        margin: 200px auto;\n        perspective: 1200px;\n      }\n  \n      .portatil {\n        width: 100%;\n        transform-style: preserve-3d;\n      }\n  \n      .pantalla {\n        width: 100%;\n        height: 340px;\n        background-color: #000000;\n        border-radius: 20px 20px 0 0;\n        overflow: hidden;\n        transform-origin: bottom center;\n        transition: transform 1s ease;\n        box-shadow: 0 20px 50px rgba(0,0,0,0.3);\n        position:relative;\n        display: flex;\n        justify-content: center;\n        align-items: center;\n        transition-delay: 0.1s;\n      }\n  \n      .pantalla video, .pantalla iframe {\n        width: 95%;\n        height: 92%;\n        object-fit: cover;\n        border: none;\n      }\n  \n      .base {\n        width: 110%;\n        height: 25px;\n        background-color: #2c2c2c;\n        border-radius: 20px;\n        position: relative;\n        left: -30px;\n      }\n  \n      .base img {\n        width: 100%;\n        height: 100%;\n        object-fit: cover;\n        border-radius: 0 0 20px 20px;\n      }\n  \n    \n      .visible .pantalla {\n        transform: rotateX(0deg);\n      }\n  \n      .pantalla {\n        transform: rotateX(-90deg);\n      }\n    </style>\n  <body>\n    <section class=\"fondo-portatil\">\n      <div class=\"texto-portatil\">\n        <h2>NUESTRAS CLASES</h2>\n      </div>\n      <div class=\"portatil-container\" id=\"portatil\">\n        <div class=\"portatil\">\n          <div class=\"pantalla\">\n          <!-- Puedes poner un iframe de YouTube o un video tag -->\n          <iframe src=\"https://www.youtube.com/embed/Re_jqeVHQbA\" frameborder=\"0\" allowfullscreen></iframe>\n          <!-- O un video local\n          <video src=\"https://www.youtube.com/watch?v=Re_jqeVHQbA\" autoplay muted loop></video>\n          -->\n        </div>\n        <div class=\"base\"></div>\n      </div>\n    </section>\n  \n    <script>\n      const portatil = document.getElementById(\'portatil\');\n  \n      function checkScroll() {\n        const rect = portatil.getBoundingClientRect();\n        const windowHeight = window.innerHeight;\n        if (rect.top < windowHeight - 100) {\n          portatil.classList.add(\'visible\');\n        } else {\n          portatil.classList.remove(\'visible\');\n        }\n      }\n  \n      window.addEventListener(\'scroll\', checkScroll);\n      checkScroll();\n    </script>\n  </body>\n  </html>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n  <meta charset=\"UTF-8\">\n  <title>Comparación Antes y Después</title>\n  <style>\n    :root {\n      --color-primario: #2ebdc2;\n      --color-secundario: #f0f0f0;\n      --color-terciario: #202020;\n      --color-caracter: #00305e;\n      --color-suave: #e0ffff;\n      --color-texto-alt: #202020;\n    }\n\n    .titulo-antes-despues {\n      font-family: \'Audiowide\', sans-serif;\n      font-size: 3rem;\n      color: var(--color-caracter);\n      background-color: var(--color-suave);\n      text-align: center;\n      padding: 200px 0px 100px;\n      margin-top: -200px;\n      width: 100vw;\n      max-width: 100vw;\n\n    }\n\n    .contenedor-antes-despues {\n      display: flex;\n      flex-direction: row;\n      justify-content: center;\n      flex-wrap: wrap;\n      gap: 100px;\n      background-color: var(--color-suave);\n      padding: 200px 100px 100px;\n      margin: -7px;\n      margin-top: -200px;\n    }\n\n    .comparison-container {\n      position: relative;\n      width: 350px;\n      height: 600px;\n      overflow: hidden;\n      border-radius: 15px;\n    }\n\n    .comparison-container img {\n      position: absolute;\n      top: 0;\n      left: 0;\n      width: 100%;\n      height: 100%;\n      object-fit: cover;\n      user-select: none;\n      pointer-events: none;\n    }\n\n    .after-image {\n      clip-path: inset(0 50% 0 0);\n      z-index: 2;\n    }\n\n    .slider {\n      position: absolute;\n      top: 0;\n      left: 50%;\n      transform: translateX(-1.5px);\n      width: 4px;\n      height: 100%;\n      background-color: var(--color-primario);\n      z-index: 3;\n      cursor: grab;\n    }\n\n    .slider-icon {\n      position: absolute;\n      top: 50%;\n      left: 50%;\n      transform: translate(-50%, -50%);\n      width: 30px;\n      height: 30px;\n      color: var(--color-suave);\n      background-color: var(--color-caracter);\n      border: 2px solid var(--color-primario);\n      border-radius: 15px;\n      display: flex;\n      justify-content: center;\n      align-items: center;\n      font-size: 18px;\n      user-select: none;\n      pointer-events: none;\n    }\n  </style>\n</head>\n<body>\n  <h2 class=\"titulo-antes-despues\">CAMBIA TU VIDA PARA SIEMPRE</h2>\n  <div class=\"contenedor-antes-despues\">\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Laura-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Laura-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Antonia-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Antonia-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/jesica-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/jesica-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Cristina-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Cristina-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Vale-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Vale-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n\n    <div class=\"comparison-container\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Tina-despues.jpg\" alt=\"Antes\" class=\"before-image\">\n      <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/Tina-antes.jpg\" alt=\"Después\" class=\"after-image\">\n      <div class=\"slider\">\n        <div class=\"slider-icon\">☰</div>\n      </div>\n    </div>\n  </div>\n\n  <script>\n    const containers = document.querySelectorAll(\'.comparison-container\');\n\n    containers.forEach(container => {\n      const afterImage = container.querySelector(\'.after-image\');\n      const slider = container.querySelector(\'.slider\');\n\n      let isDragging = false;\n\n      slider.addEventListener(\'mousedown\', () => {\n        isDragging = true;\n        slider.style.cursor = \'grabbing\';\n      });\n\n      document.addEventListener(\'mouseup\', () => {\n        isDragging = false;\n        slider.style.cursor = \'grab\';\n      });\n\n      container.addEventListener(\'mousemove\', (e) => {\n        if (!isDragging) return;\n\n        const rect = container.getBoundingClientRect();\n        let x = e.clientX - rect.left;\n        x = Math.max(0, Math.min(x, rect.width));\n        afterImage.style.clipPath = `inset(0 ${rect.width - x}px 0 0)`;\n        slider.style.left = `${x}px`;\n      });\n    });\n  </script>\n</body>\n</html>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<link href=\"https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100..900;1,100..900&display=swap\" rel=\"stylesheet\">\n  <h2 class=\"titulo-planes-entrenamiento\" id=\"planes\">PERSONALIZA TU PLAN</h2>  \n  <div class=\"contenedor-tarjetas-3columnas\">\n      <div class=\"tarjeta-estilo\">\n        <div class=\"tarjeta-imagen\">\n          <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/mujer-haciendo-deporte-gimnasio-1.jpg\"/>\n        </div>\n        <div class=\"tarjeta-contenido\">\n          <a href=\"http://localhost/wordpress/2025/05/08/edad-18-30/\" class=\"planes\">\n            <div class=\"card-header\">\n              <div class=\"highlight\"> 18-30 años</div>\n            </div>\n      <div class=\"card-body\">\n        <h2>Entrenamiento de Alto Rendimiento</h2>\n        <ul>\n          <li>Rutinas de fuerza e hipertrofia</li>\n          <li>Entrenamientos HIIT y funcionales</li>\n          <li>Alta frecuencia semanal</li>\n          <li>Enfoque en desarrollo muscular</li>\n          <li>Complemento con cardio intenso</li>\n        </ul>\n      </div>\n          </a> \n      </div>\n      </div>\n\n      <div class=\"tarjeta-estilo\">\n        <div class=\"tarjeta-imagen\">\n          <img src=\"http://localhost/wordpress/wp-content/uploads/2025/04/pexels-photo-2247179-1.jpeg\" alt=\"Tarjeta 2\" class=\"spectra-image\"/>\n        </div>\n        <div class=\"tarjeta-contenido\">\n          <a href=\"http://l