(142, '_site_transient_community-events-d41d8cd98f00b204e9800998ecf8427e', 'a:4:{s:9:\"sandboxed\";b:0;s:5:\"error\";N;s:8:\"location\";a:1:{s:2:\"ip\";b:0;}s:6:\"events\";a:3:{i:0;a:10:{s:4:\"type\";s:8:\"wordcamp\";s:5:\"title\";s:48:\"Jornada de Disseny Web amb WordPress Lleida 2025\";s:3:\"url\";s:49:\"https://events.wordpress.org/lleida/2025/disseny/\";s:6:\"meetup\";N;s:10:\"meetup_url\";N;s:4:\"date\";s:19:\"2025-04-09 09:00:00\";s:8:\"end_date\";s:19:\"2025-04-09 09:00:00\";s:20:\"start_unix_timestamp\";i:1744182000;s:18:\"end_unix_timestamp\";i:1744182000;s:8:\"location\";a:4:{s:8:\"location\";s:13:\"Lleida, Spain\";s:7:\"country\";s:2:\"ES\";s:8:\"latitude\";d:41.6076673;s:9:\"longitude\";d:0.6229487;}}i:1;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:80:\"Charla \"Cada juego, un Vietnam\" sobre Marketing para Videojuegos con Sam Sempere\";s:3:\"url\";s:58:\"https://www.meetup.com/wordpress-alicante/events/305596065\";s:6:\"meetup\";s:18:\"WordPress Alicante\";s:10:\"meetup_url\";s:42:\"https://www.meetup.com/wordpress-alicante/\";s:4:\"date\";s:19:\"2025-04-10 19:00:00\";s:8:\"end_date\";s:19:\"2025-04-10 20:30:00\";s:20:\"start_unix_timestamp\";i:1744304400;s:18:\"end_unix_timestamp\";i:1744309800;s:8:\"location\";a:4:{s:8:\"location\";s:14:\"Alacant, Spain\";s:7:\"country\";s:2:\"es\";s:8:\"latitude\";d:38.346527;s:9:\"longitude\";d:-0.483857;}}i:2;a:10:{s:4:\"type\";s:8:\"wordcamp\";s:5:\"title\";s:16:\"WordCamp Málaga\";s:3:\"url\";s:33:\"https://malaga.wordcamp.org/2025/\";s:6:\"meetup\";N;s:10:\"meetup_url\";N;s:4:\"date\";s:19:\"2025-04-26 08:00:00\";s:8:\"end_date\";s:19:\"2025-04-27 00:00:00\";s:20:\"start_unix_timestamp\";i:1745647200;s:18:\"end_unix_timestamp\";i:1745704800;s:8:\"location\";a:4:{s:8:\"location\";s:22:\"Torremolinos (Málaga)\";s:7:\"country\";s:2:\"ES\";s:8:\"latitude\";d:36.6026443;s:9:\"longitude\";d:-4.5128589;}}}}', 'off'),
